import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Grid, 
  Card, 
  CardContent, 
  Divider, 
  Avatar, 
  Chip, 
  TextField, 
  MenuItem, 
  IconButton, 
  Tooltip, 
  CircularProgress, 
  alpha, 
  useTheme 
} from '@mui/material';
import { 
  Timeline, 
  TrendingUp, 
  Search, 
  Refresh, 
  ArrowUpward, 
  ArrowDownward, 
  FilterList, 
  History 
} from '@mui/icons-material';
import { motion } from 'framer-motion';
import axios from 'axios';
import { format } from 'date-fns';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip as ChartTooltip, Legend } from 'chart.js';

// Register ChartJS components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, ChartTooltip, Legend);

// Motion components
const MotionContainer = motion(Container);
const MotionCard = motion(Card);

const Predictions = () => {
  const theme = useTheme();
  const [predictions, setPredictions] = useState([]);
  const [filteredPredictions, setFilteredPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('newest');

  useEffect(() => {
    fetchPredictions();
  }, []);

  useEffect(() => {
    // Filter and sort predictions when search term or sort option changes
    let filtered = [...predictions];

    // Apply search filter
    if (searchTerm) {
      filtered = filtered.filter(pred => 
        pred.ticker.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply sorting
    filtered = sortPredictions(filtered, sortBy);

    setFilteredPredictions(filtered);
  }, [predictions, searchTerm, sortBy]);

  const fetchPredictions = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://127.0.0.1:5000/api/predictions');
      if (response.data.status === 'success') {
        setPredictions(response.data.predictions);
        setFilteredPredictions(response.data.predictions);
      } else {
        setError('Failed to fetch predictions');
      }
    } catch (err) {
      setError('Error connecting to server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const sortPredictions = (preds, sortOption) => {
    switch (sortOption) {
      case 'newest':
        return [...preds].sort((a, b) => new Date(b.prediction_date) - new Date(a.prediction_date));
      case 'oldest':
        return [...preds].sort((a, b) => new Date(a.prediction_date) - new Date(b.prediction_date));
      case 'ticker':
        return [...preds].sort((a, b) => a.ticker.localeCompare(b.ticker));
      case 'highestChange':
        return [...preds].sort((a, b) => {
          const aChange = getPercentageChange(a);
          const bChange = getPercentageChange(b);
          return bChange - aChange;
        });
      default:
        return preds;
    }
  };

  const getPercentageChange = (prediction) => {
    if (!prediction.predictions || prediction.predictions.length === 0) return 0;
    const currentPrice = prediction.current_price;
    const lastPredictedPrice = prediction.predictions[prediction.predictions.length - 1].price;
    return ((lastPredictedPrice - currentPrice) / currentPrice) * 100;
  };

  const handleRefresh = () => {
    fetchPredictions();
  };

  const getChartData = (prediction) => {
    // Create data points for chart
    const dates = prediction.predictions.map(p => format(new Date(p.date), 'MMM d'));
    const prices = prediction.predictions.map(p => p.price);

    // Add current price as first point
    dates.unshift('Current');
    prices.unshift(prediction.current_price);

    return {
      labels: dates,
      datasets: [
        {
          label: 'Price Prediction',
          data: prices,
          borderColor: getPercentageChange(prediction) >= 0 ? theme.palette.success.main : theme.palette.error.main,
          backgroundColor: alpha(getPercentageChange(prediction) >= 0 ? theme.palette.success.main : theme.palette.error.main, 0.1),
          tension: 0.4,
          fill: true,
        }
      ]
    };
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        backgroundColor: alpha(theme.palette.background.paper, 0.9),
        titleColor: theme.palette.text.primary,
        bodyColor: theme.palette.text.secondary,
        borderColor: theme.palette.divider,
        borderWidth: 1,
        padding: 12,
        boxPadding: 6,
        usePointStyle: true,
      },
    },
    scales: {
      x: {
        grid: {
          display: false,
        },
      },
      y: {
        grid: {
          color: alpha(theme.palette.divider, 0.1),
        },
      },
    },
    elements: {
      point: {
        radius: 3,
        hoverRadius: 5,
      },
    },
  };

  return (
    <MotionContainer 
      maxWidth="lg" 
      sx={{ py: 6 }}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Box
            sx={{
              display: "flex",
              alignItems: "center",
              mb: 2,
              justifyContent: "space-between",
            }}
          >
            <Box sx={{ display: "flex", alignItems: "center" }}>
              <Avatar 
                sx={{ 
                  bgcolor: theme.palette.primary.main, 
                  mr: 2,
                  width: 48,
                  height: 48,
                  boxShadow: `0 4px 12px ${alpha(theme.palette.primary.main, 0.3)}`,
                }}
              >
                <History />
              </Avatar>
              <Typography 
                variant="h4" 
                fontWeight="700"
                sx={{
                  background: `linear-gradient(45deg, ${theme.palette.primary.dark}, ${theme.palette.primary.main})`,
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  letterSpacing: '-0.5px'
                }}
              >
                Prediction History
              </Typography>
            </Box>
            <Tooltip title="Refresh">
              <IconButton 
                onClick={handleRefresh}
                sx={{ 
                  bgcolor: alpha(theme.palette.primary.main, 0.1),
                  transition: 'all 0.2s',
                  '&:hover': {
                    transform: 'rotate(45deg)',
                    bgcolor: alpha(theme.palette.primary.main, 0.2),
                  }
                }}
              >
                <Refresh />
              </IconButton>
            </Tooltip>
          </Box>
          <Divider sx={{ mb: 4 }} />
        </Grid>

        {/* Search and Filter */}
        <Grid item xs={12}>
          <MotionCard
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 }}
            elevation={3}
            sx={{ 
              p: 3, 
              borderRadius: 4,
              boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
              border: `1px solid ${alpha(theme.palette.primary.main, 0.1)}`,
            }}
          >
            <Grid container spacing={2} alignItems="center">
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  placeholder="Search by ticker..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  InputProps={{
                    startAdornment: <Search sx={{ mr: 1, color: theme.palette.text.secondary }} />,
                    sx: {
                      borderRadius: 2,
                      bgcolor: alpha(theme.palette.background.default, 0.8),
                    }
                  }}
                  variant="outlined"
                  size="small"
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: { xs: 'flex-start', md: 'flex-end' } }}>
                  <FilterList sx={{ mr: 1, color: theme.palette.text.secondary }} />
                  <TextField
                    select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    variant="outlined"
                    size="small"
                    sx={{ minWidth: 200 }}
                    InputProps={{
                      sx: {
                        borderRadius: 2,
                        bgcolor: alpha(theme.palette.background.default, 0.8),
                      }
                    }}
                  >
                    <MenuItem value="newest">Newest First</MenuItem>
                    <MenuItem value="oldest">Oldest First</MenuItem>
                    <MenuItem value="ticker">Ticker (A-Z)</MenuItem>
                    <MenuItem value="highestChange">Highest Change</MenuItem>
                  </TextField>
                </Box>
              </Grid>
            </Grid>
          </MotionCard>
        </Grid>

        {/* Predictions List */}
        <Grid item xs={12}>
          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', my: 8 }}>
              <CircularProgress />
            </Box>
          ) : error ? (
            <MotionCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              sx={{ p: 4, borderRadius: 4, bgcolor: alpha(theme.palette.error.main, 0.1), color: theme.palette.error.main }}
            >
              <Typography variant="h6">{error}</Typography>
              <Typography variant="body2">Please try again later or contact support.</Typography>
            </MotionCard>
          ) : filteredPredictions.length === 0 ? (
            <MotionCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              sx={{ p: 4, borderRadius: 4, bgcolor: alpha(theme.palette.info.main, 0.1) }}
            >
              <Typography variant="h6">No predictions found</Typography>
              <Typography variant="body2">Try adjusting your search criteria or make new predictions.</Typography>
            </MotionCard>
          ) : (
            <Grid container spacing={3}>
              {filteredPredictions.map((prediction, index) => (
                <Grid item xs={12} md={6} key={index}>
                  <MotionCard
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.1 + index * 0.05 }}
                    elevation={3}
                    sx={{ 
                      borderRadius: 4,
                      overflow: 'hidden',
                      boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        transform: 'translateY(-5px)',
                        boxShadow: '0 12px 30px rgba(0,0,0,0.15)',
                      },
                      position: 'relative',
                      '&::before': {
                        content: '""',
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '4px',
                        background: getPercentageChange(prediction) >= 0
                          ? `linear-gradient(90deg, ${theme.palette.success.light}, ${theme.palette.success.main})`
                          : `linear-gradient(90deg, ${theme.palette.error.light}, ${theme.palette.error.main})`,
                      }
                    }}
                  >
                    <CardContent sx={{ p: 3 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                        <Typography variant="h6" fontWeight="700">
                          {prediction.ticker}
                        </Typography>
                        <Chip 
                          icon={getPercentageChange(prediction) >= 0 ? <ArrowUpward fontSize="small" /> : <ArrowDownward fontSize="small" />}
                          label={`${getPercentageChange(prediction).toFixed(2)}%`}
                          color={getPercentageChange(prediction) >= 0 ? "success" : "error"}
                          sx={{ 
                            fontWeight: 'bold',
                            borderRadius: '8px',
                            px: 1
                          }}
                        />
                      </Box>

                      <Box sx={{ mb: 2 }}>
                        <Typography variant="body2" color="text.secondary">
                          Predicted on {format(new Date(prediction.prediction_date), 'PPP p')}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          Future days: {prediction.future_days}
                        </Typography>
                      </Box>

                      <Box sx={{ height: 200, mb: 2 }}>
                        <Line data={getChartData(prediction)} options={chartOptions} />
                      </Box>

                      <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
                        <Box>
                          <Typography variant="body2" color="text.secondary">Current Price</Typography>
                          <Typography variant="h6" fontWeight="600">₹{prediction.current_price.toFixed(2)}</Typography>
                        </Box>
                        <Box sx={{ textAlign: 'right' }}>
                          <Typography variant="body2" color="text.secondary">Final Prediction</Typography>
                          <Typography 
                            variant="h6" 
                            fontWeight="600"
                            color={getPercentageChange(prediction) >= 0 ? "success.main" : "error.main"}
                          >
                            ₹{prediction.predictions[prediction.predictions.length - 1].price.toFixed(2)}
                          </Typography>
                        </Box>
                      </Box>
                    </CardContent>
                  </MotionCard>
                </Grid>
              ))}
            </Grid>
          )}
        </Grid>
      </Grid>
    </MotionContainer>
  );
};

export default Predictions;
