import React, { useState, useEffect } from "react";
import {
  Box,
  Grid,
  Divider,
  Container,
  useMediaQuery,
  useTheme,
  Paper,
  Typography,
  Fade,
  Avatar,
  IconButton,
  Tooltip,
  Badge,
  Card,
  CardContent,
} from "@mui/material";
import Header from "../components/Header";
import MarketSummary from "../components/MarketSummary";
import PortfolioPerformance from "../components/PortfolioPerformance";
import PortfolioAllocation from "../components/PortfolioAllocation";
import QuickActions from "../components/QuickActions";
import MarketNews from "../components/MarketNews";
import {
  Dashboard as DashboardIcon,
  Refresh,
  Notifications,
  TrendingUp,
} from "@mui/icons-material";
import { motion } from "framer-motion";
import {
  fetchMarketNews,
  fetchMarketData,
  fetchPerformanceData,
  fetchPortfolioData,
  fetchMarketTrends,
  fetchAiInsights,
} from "../api/dataFetchers";

const MotionCard = motion(Paper);

const Dashboard = () => {
  const [marketNews, setMarketNews] = useState([]);
  const [marketData, setMarketData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newsLoading, setNewsLoading] = useState(true);
  const [favorites, setFavorites] = useState([]);
  const [refreshing, setRefreshing] = useState(false);
  const [performanceData, setPerformanceData] = useState([]);
  const [portfolioData, setPortfolioData] = useState([]);
  const [marketTrends, setMarketTrends] = useState([]);
  const [aiInsights, setAiInsights] = useState([]);

  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));
  const isTablet = useMediaQuery(theme.breakpoints.between("sm", "md"));

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    setLoading(true);
    setNewsLoading(true);

    try {
      const newsData = await fetchMarketNews();
      setMarketNews(newsData);

      const marketDataResult = await fetchMarketData();
      setMarketData(marketDataResult);

      const performanceResult = await fetchPerformanceData();
      setPerformanceData(performanceResult);

      const portfolioResult = await fetchPortfolioData();
      setPortfolioData(portfolioResult);

      const trendsResult = await fetchMarketTrends();
      setMarketTrends(trendsResult);

      const insightsResult = await fetchAiInsights();
      setAiInsights(insightsResult);
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
      setNewsLoading(false);
    }
  };

  const handleRefresh = () => {
    setRefreshing(true);
    fetchAllData();
    setTimeout(() => setRefreshing(false), 1500);
  };

  const toggleFavorite = (index) => {
    setFavorites(
      favorites.includes(index)
        ? favorites.filter((i) => i !== index)
        : [...favorites, index]
    );
  };

  return (
    <Box sx={{ flexGrow: 1, minHeight: "100vh" }}>
      <Container maxWidth="xl" sx={{ py: 3 }}>
        <Fade in={!loading} timeout={800}>
          <Box
            sx={{
              p: { xs: 2, md: 3 },
              borderRadius: 3,
            }}
          >
            <Grid container spacing={3}>
              {/* Header with Refresh Button */}
              <Grid item xs={12}>
                <Box
                  sx={{
                    display: "flex",
                    alignItems: "center",
                    mb: 2,
                    justifyContent: "space-between",
                  }}
                >
                  <Box sx={{ display: "flex", alignItems: "center" }}>
                    <Avatar
                      sx={{
                        bgcolor: theme.palette.primary.main,
                        mr: 2,
                        boxShadow: "0 4px 8px rgba(26, 35, 126, 0.2)",
                      }}
                    >
                      <DashboardIcon />
                    </Avatar>
                    <Typography variant="h4" fontWeight="600">
                      Market Dashboard
                    </Typography>
                  </Box>
                  <Box>
                    <Tooltip title="Notifications">
                      <IconButton
                        sx={{
                          mr: 1,
                          transition: "all 0.2s",
                          "&:hover": {
                            transform: "scale(1.05)",
                          },
                        }}
                      >
                        <Notifications />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Refresh Data">
                      <IconButton
                        onClick={handleRefresh}
                        disabled={refreshing}
                        sx={{
                          transition: "all 0.2s",
                          "&:hover": {
                            transform: "scale(1.05)",
                          },
                        }}
                      >
                        <Refresh
                          sx={{
                            animation: refreshing
                              ? "spin 1s linear infinite"
                              : "none",
                            "@keyframes spin": {
                              "0%": { transform: "rotate(0deg)" },
                              "100%": { transform: "rotate(360deg)" },
                            },
                          }}
                        />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </Box>
                <Divider sx={{ mb: 3 }} />
              </Grid>

              {/* Dashboard Introduction */}
              <Grid item xs={12}>
                <Card
                  elevation={0}
                  sx={{
                    mb: 3,
                    borderRadius: 2,
                    border: "1px solid rgba(0, 0, 0, 0.12)",
                  }}
                >
                  <CardContent>
                    <Box sx={{ display: "flex", alignItems: "center", mb: 1 }}>
                      <TrendingUp sx={{ color: "#1a237e", mr: 1 }} />
                      <Typography
                        variant="h5"
                        fontWeight="600"
                        sx={{
                          letterSpacing: "-0.5px",
                        }}
                      >
                        Financial Dashboard
                      </Typography>
                    </Box>
                    <Typography
                      variant="subtitle1"
                      color="text.secondary"
                      sx={{ maxWidth: "800px" }}
                    >
                      Track your investments, analyze market trends, and get
                      AI-powered insights to optimize your portfolio.
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>

              {/* Market Summary Cards - Full width and prominent */}
              <Grid item xs={12}>
                <MarketSummary marketTrends={marketTrends} />
              </Grid>

              {/* Main Content Area */}
              <Grid container item spacing={3}>
                {/* Left Column */}
                <Grid item xs={12} lg={8}>
                  <Grid container spacing={3}>
                    {/* Portfolio Performance Chart */}
                    <Grid item xs={12} md={8}>
                      <MotionCard
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.1 }}
                        elevation={2}
                        sx={{
                          borderRadius: 2,
                          overflow: "hidden",
                          height: "100%",
                          transition: "transform 0.3s, box-shadow 0.3s",
                          "&:hover": {
                            boxShadow: "0 8px 24px rgba(0,0,0,0.12)",
                            transform: "translateY(-4px)",
                          },
                        }}
                      >
                        <PortfolioPerformance
                          performanceData={performanceData}
                        />
                      </MotionCard>
                    </Grid>

                    {/* Portfolio Allocation */}
                    <Grid item xs={12} md={4}>
                      <MotionCard
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.2 }}
                        elevation={2}
                        sx={{
                          borderRadius: 2,
                          overflow: "hidden",
                          height: "100%",
                          transition: "transform 0.3s, box-shadow 0.3s",
                          "&:hover": {
                            boxShadow: "0 8px 24px rgba(0,0,0,0.12)",
                            transform: "translateY(-4px)",
                          },
                        }}
                      >
                        <PortfolioAllocation portfolioData={portfolioData} />
                      </MotionCard>
                    </Grid>

                    {/* Quick Actions */}
                    <Grid item xs={12}>
                      <MotionCard
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.3 }}
                        elevation={2}
                        sx={{
                          borderRadius: 2,
                          overflow: "hidden",
                          transition: "transform 0.3s, box-shadow 0.3s",
                          "&:hover": {
                            boxShadow: "0 8px 24px rgba(0,0,0,0.12)",
                            transform: "translateY(-4px)",
                          },
                        }}
                      >
                        <QuickActions />
                      </MotionCard>
                    </Grid>
                  </Grid>
                </Grid>

                {/* Right Column */}
                <Grid item xs={12} lg={4}>
                  <MotionCard
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.4 }}
                    elevation={2}
                    sx={{
                      borderRadius: 2,
                      overflow: "hidden",
                      height: "100%",
                      transition: "transform 0.3s, box-shadow 0.3s",
                      "&:hover": {
                        boxShadow: "0 8px 24px rgba(0,0,0,0.12)",
                        transform: "translateY(-4px)",
                      },
                    }}
                  >
                    <MarketNews
                      marketNews={marketNews}
                      loading={newsLoading}
                      favorites={favorites}
                      toggleFavorite={toggleFavorite}
                    />
                  </MotionCard>
                </Grid>
              </Grid>
            </Grid>
          </Box>
        </Fade>
      </Container>
    </Box>
  );
};

export default Dashboard;
