import React, { useState, useEffect } from "react";
import {
  Box,
  Typography,
  Container,
  Paper,
  Avatar,
  Grid,
  Divider,
  Button,
  TextField,
  IconButton,
  Tabs,
  Tab,
  Card,
  CardContent,
  Chip,
  Fade,
  CircularProgress,
  useTheme,
  Tooltip,
} from "@mui/material";
import {
  Edit,
  Save,
  Person,
  Notifications,
  Security,
  ShowChart,
  Favorite,
  History,
  TrendingUp,
  Settings,
} from "@mui/icons-material";
import { styled } from "@mui/material/styles";
import { motion } from "framer-motion";

const MotionCard = motion(Card);

// Mock data - would be fetched from API in a real app
const mockUserData = {
  id: "koushik",
  name: "koushik",
  email: "koushik.archy@example.com",
  joinDate: "January 15, 2023",
  avatar: "/assets/avatar.jpg",
  bio: "Passionate investor with 5 years of experience in stock markets. Focused on tech and renewable energy sectors.",
  location: "India",
  profession: "Financial Analyst",
  favoriteStocks: [
    { symbol: "AAPL", name: "Apple Inc.", change: "+2.3%" },
    { symbol: "MSFT", name: "Microsoft Corp.", change: "+1.7%" },
    { symbol: "TSLA", name: "Tesla Inc.", change: "-0.8%" },
    { symbol: "NVDA", name: "NVIDIA Corp.", change: "+3.5%" },
  ],
  recentActivity: [
    { type: "analysis", stock: "AAPL", date: "2 hours ago" },
    { type: "prediction", stock: "MSFT", date: "Yesterday" },
    { type: "watchlist", stock: "AMZN", date: "3 days ago" },
    { type: "analysis", stock: "GOOGL", date: "1 week ago" },
  ],
  performanceStats: {
    accuracy: 78,
    totalPredictions: 145,
    successfulPredictions: 113,
    averageReturn: "+12.4%",
  },
};

const StyledAvatar = styled(Avatar)(({ theme }) => ({
  width: 120,
  height: 120,
  border: `4px solid ${theme.palette.background.paper}`,
  boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
}));

const Profile = () => {
  const theme = useTheme();
  const [userData, setUserData] = useState(mockUserData);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [editedUserData, setEditedUserData] = useState(userData);

  useEffect(() => {
    // Simulate API fetch
    const fetchUserData = async () => {
      setLoading(true);
      try {
        // In a real app, this would be an API call
        setTimeout(() => {
          setUserData(mockUserData);
          setEditedUserData(mockUserData);
          setLoading(false);
        }, 1000);
      } catch (error) {
        console.error("Error fetching user data:", error);
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleEditToggle = () => {
    if (editMode) {
      // Save changes
      setUserData(editedUserData);
    }
    setEditMode(!editMode);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedUserData({
      ...editedUserData,
      [name]: value,
    });
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "80vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1, minHeight: "100vh" }}>
      <Container maxWidth="xl" sx={{ py: 3 }}>
        <Fade in={!loading} timeout={800}>
          <Box>
            {/* Profile Header */}
            <MotionCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              sx={{
                mb: 3,
                borderRadius: 3,
                overflow: "hidden",
                boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
              }}
            >
              <Box
                sx={{
                  height: 200,
                  background: "linear-gradient(90deg, #1a237e, #3949ab)",
                  position: "relative",
                }}
              />
              <Box
                sx={{
                  display: "flex",
                  flexDirection: { xs: "column", md: "row" },
                  p: 3,
                  pt: 0,
                  mt: -8,
                }}
              >
                <Box
                  sx={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: { xs: "center", md: "flex-start" },
                    mr: { md: 4 },
                  }}
                >
                  <StyledAvatar
                    src={userData.avatar}
                    alt={userData.name}
                    sx={{ mb: 2 }}
                  >
                    <Person fontSize="large" />
                  </StyledAvatar>
                  {!editMode ? (
                    <Button
                      variant="contained"
                      startIcon={<Edit />}
                      onClick={handleEditToggle}
                      sx={{
                        borderRadius: 2,
                        textTransform: "none",
                        mb: { xs: 2, md: 0 },
                      }}
                    >
                      Edit Profile
                    </Button>
                  ) : (
                    <Button
                      variant="contained"
                      color="success"
                      startIcon={<Save />}
                      onClick={handleEditToggle}
                      sx={{
                        borderRadius: 2,
                        textTransform: "none",
                        mb: { xs: 2, md: 0 },
                      }}
                    >
                      Save Changes
                    </Button>
                  )}
                </Box>
                <Box
                  sx={{
                    flexGrow: 1,
                    mt: { xs: 2, md: 8 },
                    textAlign: { xs: "center", md: "left" },
                  }}
                >
                  {!editMode ? (
                    <>
                      <Typography variant="h4" fontWeight="600">
                        {userData.name}
                      </Typography>
                      <Typography
                        variant="subtitle1"
                        color="text.secondary"
                        gutterBottom
                      >
                        {userData.profession}  {userData.location}
                      </Typography>
                      <Typography variant="body1" sx={{ mt: 1 }}>
                        {userData.bio}
                      </Typography>
                    </>
                  ) : (
                    <Grid container spacing={2}>
                      <Grid item xs={12} md={6}>
                        <TextField
                          fullWidth
                          label="Name"
                          name="name"
                          value={editedUserData.name}
                          onChange={handleInputChange}
                          variant="outlined"
                          size="small"
                          sx={{ mb: 2 }}
                        />
                        <TextField
                          fullWidth
                          label="Profession"
                          name="profession"
                          value={editedUserData.profession}
                          onChange={handleInputChange}
                          variant="outlined"
                          size="small"
                          sx={{ mb: 2 }}
                        />
                        <TextField
                          fullWidth
                          label="Location"
                          name="location"
                          value={editedUserData.location}
                          onChange={handleInputChange}
                          variant="outlined"
                          size="small"
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <TextField
                          fullWidth
                          label="Bio"
                          name="bio"
                          value={editedUserData.bio}
                          onChange={handleInputChange}
                          variant="outlined"
                          multiline
                          rows={4}
                        />
                      </Grid>
                    </Grid>
                  )}
                </Box>
              </Box>
            </MotionCard>

            {/* Profile Content */}
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Paper sx={{ borderRadius: 3 }}>
                  <Tabs
                    value={tabValue}
                    onChange={handleTabChange}
                    variant="scrollable"
                    scrollButtons="auto"
                    sx={{
                      borderBottom: 1,
                      borderColor: "divider",
                      "& .MuiTab-root": {
                        textTransform: "none",
                        minWidth: 120,
                      },
                    }}
                  >
                    <Tab
                      icon={<ShowChart />}
                      iconPosition="start"
                      label="Activity"
                    />
                    <Tab
                      icon={<Favorite />}
                      iconPosition="start"
                      label="Favorites"
                    />
                    <Tab
                      icon={<TrendingUp />}
                      iconPosition="start"
                      label="Performance"
                    />
                    <Tab
                      icon={<Settings />}
                      iconPosition="start"
                      label="Settings"
                    />
                  </Tabs>

                  {/* Activity Tab */}
                  {tabValue === 0 && (
                    <Box sx={{ p: 3 }}>
                      <Typography variant="h6" gutterBottom>
                        Recent Activity
                      </Typography>
                      <Grid container spacing={2}>
                        {userData.recentActivity.map((activity, index) => (
                          <Grid item xs={12} key={index}>
                            <MotionCard
                              initial={{ opacity: 0, y: 10 }}
                              animate={{ opacity: 1, y: 0 }}
                              transition={{ delay: index * 0.1 }}
                              sx={{ mb: 1 }}
                            >
                              <CardContent
                                sx={{
                                  display: "flex",
                                  alignItems: "center",
                                  p: 2,
                                  "&:last-child": { pb: 2 },
                                }}
                              >
                                <Avatar
                                  sx={{
                                    bgcolor:
                                      activity.type === "analysis"
                                        ? "primary.main"
                                        : activity.type === "prediction"
                                        ? "success.main"
                                        : "info.main",
                                    mr: 2,
                                  }}
                                >
                                  {activity.type === "analysis" ? (
                                    <ShowChart />
                                  ) : activity.type === "prediction" ? (
                                    <TrendingUp />
                                  ) : (
                                    <Favorite />
                                  )}
                                </Avatar>
                                <Box sx={{ flexGrow: 1 }}>
                                  <Typography variant="body1">
                                    {activity.type === "analysis"
                                      ? `Analyzed ${activity.stock}`
                                      : activity.type === "prediction"
                                      ? `Made prediction for ${activity.stock}`
                                      : `Added ${activity.stock} to watchlist`}
                                  </Typography>
                                  <Typography
                                    variant="caption"
                                    color="text.secondary"
                                  >
                                    {activity.date}
                                  </Typography>
                                </Box>
                                <Chip
                                  label={activity.stock}
                                  size="small"
                                  color="primary"
                                  variant="outlined"
                                />
                              </CardContent>
                            </MotionCard>
                          </Grid>
                        ))}
                      </Grid>
                    </Box>
                  )}

                  {/* Favorites Tab */}
                  {tabValue === 1 && (
                    <Box sx={{ p: 3 }}>
                      <Typography variant="h6" gutterBottom>
                        Favorite Stocks
                      </Typography>
                      <Grid container spacing={2}>
                        {userData.favoriteStocks.map((stock, index) => (
                          <Grid item xs={12} sm={6} md={3} key={index}>
                            <MotionCard
                              initial={{ opacity: 0, scale: 0.95 }}
                              animate={{ opacity: 1, scale: 1 }}
                              transition={{ delay: index * 0.1 }}
                              sx={{
                                height: "100%",
                                display: "flex",
                                flexDirection: "column",
                              }}
                            >
                              <CardContent>
                                <Box
                                  sx={{
                                    display: "flex",
                                    justifyContent: "space-between",
                                    alignItems: "center",
                                    mb: 1,
                                  }}
                                >
                                  <Typography
                                    variant="h6"
                                    fontWeight="bold"
                                    color="primary"
                                  >
                                    {stock.symbol}
                                  </Typography>
                                  <Chip
                                    label={stock.change}
                                    size="small"
                                    color={
                                      stock.change.startsWith("+")
                                        ? "success"
                                        : "error"
                                    }
                                  />
                                </Box>
                                <Typography variant="body2" gutterBottom>
                                  {stock.name}
                                </Typography>
                                <Box
                                  sx={{
                                    display: "flex",
                                    justifyContent: "flex-end",
                                    mt: 2,
                                  }}
                                >
                                  <Button
                                    size="small"
                                    variant="outlined"
                                    sx={{ borderRadius: 2 }}
                                  >
                                    View Analysis
                                  </Button>
                                </Box>
                              </CardContent>
                            </MotionCard>
                          </Grid>
                        ))}
                      </Grid>
                    </Box>
                  )}

                  {/* Performance Tab */}
                  {tabValue === 2 && (
                    <Box sx={{ p: 3 }}>
                      <Typography variant="h6" gutterBottom>
                        Prediction Performance
                      </Typography>
                      <Grid container spacing={3}>
                        <Grid item xs={12} md={6}>
                          <MotionCard
                            initial={{ opacity: 0, x: -20 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ delay: 0.1 }}
                          >
                            <CardContent>
                              <Typography
                                variant="subtitle1"
                                color="text.secondary"
                                gutterBottom
                              >
                                Prediction Accuracy
                              </Typography>
                              <Box
                                sx={{
                                  display: "flex",
                                  alignItems: "center",
                                  justifyContent: "center",
                                  position: "relative",
                                  my: 2,
                                }}
                              >
                                <CircularProgress
                                  variant="determinate"
                                  value={userData.performanceStats.accuracy}
                                  size={120}
                                  thickness={5}
                                  sx={{ color: "success.main" }}
                                />
                                <Box
                                  sx={{
                                    position: "absolute",
                                    display: "flex",
                                    alignItems: "center",
                                    justifyContent: "center",
                                  }}
                                >
                                  <Typography
                                    variant="h4"
                                    component="div"
                                    color="text.primary"
                                  >
                                    {`${userData.performanceStats.accuracy}%`}
                                  </Typography>
                                </Box>
                              </Box>
                              <Typography
                                variant="body2"
                                align="center"
                                color="text.secondary"
                              >
                                Based on your last{" "}
                                {userData.performanceStats.totalPredictions}{" "}
                                predictions
                              </Typography>
                            </CardContent>
                          </MotionCard>
                        </Grid>
                        <Grid item xs={12} md={6}>
                          <MotionCard
                            initial={{ opacity: 0, x: 20 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ delay: 0.2 }}
                          >
                            <CardContent>
                              <Typography
                                variant="subtitle1"
                                color="text.secondary"
                                gutterBottom
                              >
                                Performance Statistics
                              </Typography>
                              <Box sx={{ my: 2 }}>
                                <Grid container spacing={2}>
                                  <Grid item xs={6}>
                                    <Paper
                                      elevation={0}
                                      sx={{
                                        p: 2,
                                        textAlign: "center",
                                        bgcolor: "background.default",
                                        borderRadius: 2,
                                      }}
                                    >
                                      <Typography
                                        variant="h5"
                                        fontWeight="bold"
                                      >
                                        {
                                          userData.performanceStats
                                            .totalPredictions
                                        }
                                      </Typography>
                                      <Typography
                                        variant="body2"
                                        color="text.secondary"
                                      >
                                        Total Predictions
                                      </Typography>
                                    </Paper>
                                  </Grid>
                                  <Grid item xs={6}>
                                    <Paper
                                      elevation={0}
                                      sx={{
                                        p: 2,
                                        textAlign: "center",
                                        bgcolor: "background.default",
                                        borderRadius: 2,
                                      }}
                                    >
                                      <Typography
                                        variant="h5"
                                        fontWeight="bold"
                                        color="success.main"
                                      >
                                        {
                                          userData.performanceStats
                                            .successfulPredictions
                                        }
                                      </Typography>
                                      <Typography
                                        variant="body2"
                                        color="text.secondary"
                                      >
                                        Successful
                                      </Typography>
                                    </Paper>
                                  </Grid>
                                  <Grid item xs={12}>
                                    <Paper
                                      elevation={0}
                                      sx={{
                                        p: 2,
                                        textAlign: "center",
                                        bgcolor: "background.default",
                                        borderRadius: 2,
                                      }}
                                    >
                                      <Typography
                                        variant="h5"
                                        fontWeight="bold"
                                        color={
                                          userData.performanceStats.averageReturn.startsWith(
                                            "+"
                                          )
                                            ? "success.main"
                                            : "error.main"
                                        }
                                      >
                                        {
                                          userData.performanceStats
                                            .averageReturn
                                        }
                                      </Typography>
                                      <Typography
                                        variant="body2"
                                        color="text.secondary"
                                      >
                                        Average Return
                                      </Typography>
                                    </Paper>
                                  </Grid>
                                </Grid>
                              </Box>
                            </CardContent>
                          </MotionCard>
                        </Grid>
                      </Grid>
                    </Box>
                  )}

                  {/* Settings Tab */}
                  {tabValue === 3 && (
                    <Box sx={{ p: 3 }}>
                      <Typography variant="h6" gutterBottom>
                        Account Settings
                      </Typography>
                      <Grid container spacing={3}>
                        <Grid item xs={12} md={6}>
                          <MotionCard
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.1 }}
                          >
                            <CardContent>
                              <Box
                                sx={{
                                  display: "flex",
                                  alignItems: "center",
                                  mb: 2,
                                }}
                              >
                                <Person color="primary" sx={{ mr: 1 }} />
                                <Typography variant="h6">
                                  Personal Information
                                </Typography>
                              </Box>
                              <Divider sx={{ mb: 2 }} />
                              <Grid container spacing={2}>
                                <Grid item xs={12}>
                                  <TextField
                                    fullWidth
                                    label="Email"
                                    value={userData.email}
                                    variant="outlined"
                                    size="small"
                                    disabled
                                  />
                                </Grid>
                                <Grid item xs={12}>
                                  <TextField
                                    fullWidth
                                    label="Member Since"
                                    value={userData.joinDate}
                                    variant="outlined"
                                    size="small"
                                    disabled
                                  />
                                </Grid>
                                <Grid item xs={12}>
                                  <Button
                                    variant="contained"
                                    color="primary"
                                    sx={{ mt: 1, borderRadius: 2 }}
                                  >
                                    Change Password
                                  </Button>
                                </Grid>
                              </Grid>
                            </CardContent>
                          </MotionCard>
                        </Grid>
                        <Grid item xs={12} md={6}>
                          <MotionCard
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.2 }}
                          >
                            <CardContent>
                              <Box
                                sx={{
                                  display: "flex",
                                  alignItems: "center",
                                  mb: 2,
                                }}
                              >
                                <Notifications color="primary" sx={{ mr: 1 }} />
                                <Typography variant="h6">
                                  Notification Settings
                                </Typography>
                              </Box>
                              <Divider sx={{ mb: 2 }} />
                              <Grid container spacing={2}>
                                <Grid item xs={12}>
                                  <Button
                                    variant="outlined"
                                    fullWidth
                                    sx={{
                                      justifyContent: "flex-start",
                                      textTransform: "none",
                                      py: 1.5,
                                      mb: 1,
                                    }}
                                  >
                                    Email Notifications
                                  </Button>
                                </Grid>
                                <Grid item xs={12}>
                                  <Button
                                    variant="outlined"
                                    fullWidth
                                    sx={{
                                      justifyContent: "flex-start",
                                      textTransform: "none",
                                      py: 1.5,
                                      mb: 1,
                                    }}
                                  >
                                    Stock Alerts
                                  </Button>
                                </Grid>
                                <Grid item xs={12}>
                                  <Button
                                    variant="outlined"
                                    fullWidth
                                    sx={{
                                      justifyContent: "flex-start",
                                      textTransform: "none",
                                      py: 1.5,
                                    }}
                                  >
                                    Market Updates
                                  </Button>
                                </Grid>
                              </Grid>
                            </CardContent>
                          </MotionCard>
                        </Grid>
                      </Grid>
                    </Box>
                  )}
                </Paper>
              </Grid>
            </Grid>
          </Box>
        </Fade>
      </Container>
    </Box>
  );
};

export default Profile;
