import React, { useState, useEffect } from "react";
import {
  Box,
  Typography,
  Container,
  Paper,
  Grid,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  CircularProgress,
  Divider,
  Card,
  CardContent,
  Fade,
  Chip,
  Avatar,
  Tooltip,
  IconButton,
  useTheme,
  alpha,
  Zoom,
  Slide,
} from "@mui/material";
import {
  TrendingUp,
  Assessment,
  ShowChart,
  Refresh,
  BarChart,
  Timeline,
  Analytics,
  ArrowUpward,
  ArrowDownward,
  Bolt,
  Insights,
  TrendingDown,
  CandlestickChart,
  AutoGraph,
  PriceChange,
  MonetizationOn,
} from "@mui/icons-material";
import { motion } from "framer-motion";
import { styled } from "@mui/material/styles";
import { keyframes } from "@emotion/react";

// Animations
const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const float = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0px); }
`;

const shimmer = keyframes`
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
`;

const MotionCard = motion(Paper);

const GradientButton = styled(Button)(({ theme }) => ({
  borderRadius: 12,
  textTransform: "none",
  fontWeight: 700,
  fontSize: "1rem",
  padding: "12px 24px",
  background: `linear-gradient(45deg, ${theme.palette.primary.dark}, ${theme.palette.secondary.main})`,
  boxShadow: `0 8px 20px ${alpha(theme.palette.primary.main, 0.5)}`,
  transition: "all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275)",
  "&:hover": {
    boxShadow: `0 12px 28px ${alpha(theme.palette.primary.main, 0.7)}`,
    transform: "translateY(-5px) scale(1.02)",
    background: `linear-gradient(45deg, ${theme.palette.secondary.main}, ${theme.palette.primary.dark})`,
  },
  "&:active": {
    transform: "translateY(0) scale(0.98)",
  },
}));

const StatsCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2.5),
  textAlign: "center",
  borderRadius: 16,
  boxShadow: "0 10px 20px rgba(0,0,0,0.12)",
  transition: "all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275)",
  position: "relative",
  overflow: "hidden",
  "&:hover": {
    transform: "translateY(-8px) scale(1.03)",
