const StockAnalysis = () => {
  const theme = useTheme();
  const [selectedCompanies, setSelectedCompanies] = useState([]);
  const [analyzing, setAnalyzing] = useState(false);
  const [report, setReport] = useState(null);
  const [popularStocks, setPopularStocks] = useState([]);
  const [animateReport, setAnimateReport] = useState(false);

  useEffect(() => {
    // Fetch popular stocks from the backend
    const fetchPopularStocks = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/popular_stocks");
        if (response.ok) {
          const data = await response.json();
          setPopularStocks(data);
        }
      } catch (error) {
        console.error("Error fetching popular stocks:", error);
      }
    };

    fetchPopularStocks();
  }, []);

  const availableCompanies =
    popularStocks.length > 0
      ? popularStocks.map((stock) => ({ id: stock.ticker, name: stock.name }))
      : [
          { id: "TCS.NS", name: "Tata Consultancy Services" },
          { id: "HDFCBANK.NS", name: "HDFC Bank" },
          { id: "INFY.NS", name: "Infosys" },
          { id: "HINDUNILVR.NS", name: "Hindustan Unilever" },
          { id: "ICICIBANK.NS", name: "ICICI Bank" },
          { id: "SBIN.NS", name: "State Bank of India" },
          { id: "BAJFINANCE.NS", name: "Bajaj Finance" },
          { id: "WIPRO.NS", name: "Wipro" },
          { id: "AXISBANK.NS", name: "Axis Bank" },
        ];

  const handleCompanyChange = (event) => {
    setSelectedCompanies(event.target.value);
  };

  const generateReport = async () => {
    if (selectedCompanies.length === 0) return;

    setAnalyzing(true);

    try {
      const response = await fetch("http://127.0.0.1:5000/api/analyze", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify({
          ticker: selectedCompanies[0], // Currently analyzing first selected company
          future_days: 30,
          force_retrain: false,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      setReport({
        timestamp: new Date().toISOString(),
        companies: selectedCompanies.map((companyId) => {
          const company = availableCompanies.find((c) => c.id === companyId);
          return {
            id: companyId,
            name: company.name,
            currentPrice: data.current_price,
            priceChange: (
              ((data.basic_stats.last - data.basic_stats.first) /
                data.basic_stats.first) *
              100
            ).toFixed(2),
            recommendation:
              data.model_metrics["R² Score"] > 0.7 ? "Buy" : "Hold",
            analysis: {
              technical: {
                macd: data.model_metrics["Mean Absolute Error"],
                rsi: data.model_metrics["Root Mean Squared Error"],
                movingAverage: data.model_metrics["Mean Squared Error"],
              },
              fundamental: {
                pe: data.basic_stats.std.toFixed(2),
                eps: data.basic_stats.mean.toFixed(2),
                marketCap: data.basic_stats.last,
              },
              sentiment: {
                score: (
                  parseFloat(data.model_metrics["R² Score"]) * 10
                ).toFixed(1),
                newsImpact:
                  parseFloat(data.average_daily_return) > 0
                    ? "Positive"
                    : "Negative",
              },
            },
            prediction: {
              shortTerm: data.future_predictions[0].price,
              midTerm: data.future_predictions[14].price,
              longTerm: data.future_predictions[29].price,
            },
          };
        }),
      });

      // Trigger animation for report
      setAnimateReport(true);
    } catch (error) {
      console.error("Error generating report:", error);
    } finally {
      setAnalyzing(false);
    }
  };

  const handleRefresh = () => {
    setAnimateReport(false);
    setTimeout(() => {
      setReport(null);
    }, 300);
  };

