                                      <Typography
                                        variant="body2"
                                        sx={{ mb: 1 }}
                                      >
                                        <strong>P/E Ratio:</strong>{" "}
                                        {company.analysis.fundamental.pe}
                                      </Typography>
                                      <Typography
                                        variant="body2"
                                        sx={{ mb: 1 }}
                                      >
                                        <strong>EPS:</strong>{" "}
                                        {company.analysis.fundamental.eps}
                                      </Typography>
                                      <Typography variant="body2">
                                        <strong>Market Cap:</strong>{" "}
                                        {company.analysis.fundamental.marketCap}
                                      </Typography>
                                    </Box>
                                  </Grid>

                                  <Grid item xs={12} sm={4}>
                                    <Box
                                      sx={{
                                        p: 2,
                                        borderRadius: 2,
                                        bgcolor: alpha(
                                          company.analysis.sentiment
                                            .newsImpact === "Positive"
                                            ? theme.palette.success.main
                                            : theme.palette.error.main,
                                          0.05
                                        ),
                                        height: "100%",
                                      }}
                                    >
                                      <Typography
                                        variant="subtitle2"
                                        color={
                                          company.analysis.sentiment
                                            .newsImpact === "Positive"
                                            ? "success.dark"
                                            : "error.dark"
                                        }
                                        fontWeight="600"
                                        sx={{ mb: 1.5 }}
                                      >
                                        Sentiment
                                      </Typography>
                                      <Typography
                                        variant="body2"
                                        sx={{ mb: 1 }}
                                      >
                                        <strong>Score:</strong>{" "}
                                        {company.analysis.sentiment.score}/10
                                      </Typography>
                                      <Typography variant="body2">
                                        <strong>News Impact:</strong>{" "}
                                        {company.analysis.sentiment.newsImpact}
                                      </Typography>
                                    </Box>
                                  </Grid>
                                </Grid>

                                <Divider sx={{ my: 3 }} />

                                <Typography
                                  variant="subtitle1"
                                  fontWeight="700"
                                  gutterBottom
                                  color="primary.dark"
                                >
                                  Price Predictions
                                </Typography>
                                <Grid container spacing={2}>
                                  <Grid item xs={4}>
                                    <StatsCard>
                                      <Typography
                                        variant="caption"
                                        color="text.secondary"
                                        fontWeight="500"
                                      >
                                        Short Term
                                      </Typography>
                                      <Typography
                                        variant="subtitle1"
                                        fontWeight="bold"
                                        color="primary.dark"
                                      >
                                        {company.prediction.shortTerm}
                                      </Typography>
                                    </StatsCard>
                                  </Grid>
                                  <Grid item xs={4}>
                                    <StatsCard>
                                      <Typography
                                        variant="caption"
                                        color="text.secondary"
                                        fontWeight="500"
                                      >
                                        Mid Term
                                      </Typography>
                                      <Typography
                                        variant="subtitle1"
                                        fontWeight="bold"
                                        color="primary.dark"
                                      >
                                        {company.prediction.midTerm}
                                      </Typography>
                                    </StatsCard>
                                  </Grid>
                                  <Grid item xs={4}>
                                    <StatsCard>
                                      <Typography
                                        variant="caption"
                                        color="text.secondary"
                                        fontWeight="500"
                                      >
                                        Long Term
                                      </Typography>
                                      <Typography
                                        variant="subtitle1"
                                        fontWeight="bold"
                                        color="primary.dark"
                                      >
                                        {company.prediction.longTerm}
                                      </Typography>
                                    </StatsCard>
                                  </Grid>
                                </Grid>
                              </CardContent>
                            </Card>
                          </Grid>
                        ))}
                      </Grid>
                    </MotionCard>
                  </Zoom>
                </Grid>
              )}
            </Grid>
          </Box>
        </Fade>
      </Container>
    </Box>
  );
};

export default StockAnalysis;
